import { Grid } from '@mui/material';
import React, { useState } from 'react'
import assets from "../assets"
import SigninForm from '../components/SigninForm';
import SignupForm from '../components/SingupForm';

export const ScreenMode = {
    SIGN_IN:"SIGN_IN",
    SIGN_UP:"SIGN_UP"
};
const SigninPage = () => {
    const [backgroundImage, setbackgroundImage] = useState(assets.images.singinBg);
    const [currMode, setCurrMode] = useState(ScreenMode.SIGN_IN);
  return (
    <Grid container sx={{height:"100vh"}}>
        <Grid item xs={4}>{
            currMode === ScreenMode.SIGN_IN ? (<SigninForm />):(<SignupForm />)
        }
        </Grid>
        <Grid item xs={8} sx={{position:"relative",backgroundImage:`url(${backgroundImage})`,backgroundPosition:"center",backgroundSize:"cover",backgroundRepeat:"no-repeat"}}></Grid>
    </Grid>
  )
}

export default SigninPage;
