import React, { useState } from 'react'
import './Navbar.css'
import logo from '../../assets/images/AJ.png'
import search_icon_light from '../../assets/images/search-w.png'
import search_icon_dark from '../../assets/images/search-b.png'
import toggle_light from '../../assets/images/night.png'
import toggle_dark from '../../assets/images/day.png'


const Navbar = ({theme,setTheme}) => {
    const toggle_mode =()=>{
        theme == 'light' ? setTheme('dark') : setTheme('light');
    }
    const [navActive, setNavActive] = useState('nav-menu');
    const navToggle = () =>{
        navActive === 'nav-menu' ? setNavActive('nav-menu nav-active'):setNavActive('nav-menu');
    }
  return (
    <div className='navbar'>
      <img src={logo} alt="" className='logo'/>
      <ul className={navActive}>
        <li className='nav-item'><a href='#' className='nav-link'>Home</a></li>
        <li className='nav-item'><a href='#' className='nav-link'>Products</a></li>
        <li className='nav-item'><a href='#' className='nav-link'>Features</a></li>
        <li className='nav-item'><a href='#' className='nav-link'>About</a></li>
      </ul>
      <div onclick ={navToggle} className='nav-toggler'>
        <div className='line1'></div>
        <div className='line2'></div>
        <div className='line3'></div>
      </div>

      <div className='search-box'>
        <input type="text" placeholder='Search'/>
        <img src={theme == 'light' ?search_icon_light :search_icon_dark

        } alt="logo" />
      </div>
      <img src={theme == 'light' ?toggle_light :toggle_dark} alt="" className='toggle-icon' onClick={()=>{toggle_mode()}}/>

    </div>
  );
}

export default Navbar;
