import { Box, Grid, colors } from '@mui/material';
import React, { useState } from 'react'
import assets from "../assets"
import SigninForm from '../components/SigninForm';
import SignupForm from '../components/SignupForm';

export const ScreenMode = {
    SIGN_IN:"SIGN_IN",
    SIGN_UP:"SIGN_UP"
};
const SigninPage = () => {
    const [left, setLeft] = useState(0);
    const [right, setRight] = useState("unset");
    const [width, setWidth] = useState(0);
    const [backgroundImage, setbackgroundImage] = useState(assets.images.signinBg);
    const [currMode, setCurrMode] = useState(ScreenMode.SIGN_IN);

    const onSwitchMode = (mode) => {


        setWidth(100);
        const timeout1=setTimeout(() => {
            setCurrMode(mode);
            setbackgroundImage(mode === ScreenMode.SIGN_IN? assets.images.signinBg : assets.images.signupBg);
        }, 1100);
        const timeout2=setTimeout(() => {
            setLeft("unset");
            setRight(0);
            setWidth(0);
        }, 1200);
        const timeout3=setTimeout(() => {
            setRight("unset");
            setLeft(0);
        }, 2500);
        return () =>{
            clearTimeout(timeout1);
            clearTimeout(timeout2);
            clearTimeout(timeout3);
        }
    }

  return (
    <Grid container sx={{height:"100vh"}}>
        <Grid item xs={4}>{
            currMode === ScreenMode.SIGN_IN ? (<SigninForm onSwitchMode={onSwitchMode}  />):(<SignupForm onSwitchMode={onSwitchMode}  />)
        }
        <Box sx={{position:"absolute",top:0,right:right,left:left,width:`${width}%`,height:"100%", bgcolor:colors.grey[800],transition:"all 1s ease-in-out"}}></Box>
        </Grid>
        <Grid item xs={8} sx={{position:"relative",backgroundImage:`url(${backgroundImage})`,backgroundPosition:"center",backgroundSize:"cover",backgroundRepeat:"no-repeat"}}>
        <Box sx={{position:"absolute",top:0,right:right,left:left,width:`${width}%`,height:"100%", bgcolor:colors.common.white,transition:"all 1s ease-in-out"}}></Box>
        </Grid>
    </Grid>
  )
}

export default SigninPage;
