import React, { useState } from 'react'
import './Navbar2.css'
import logo from '../../assets/images/AJ.png'
import search_icon_light from '../../assets/images/search-w.png'
import search_icon_dark from '../../assets/images/search-b.png'
import toggle_light from '../../assets/images/night.png'
import toggle_dark from '../../assets/images/day.png'


const Navbar = ({theme,setTheme}) => {
    const toggle_mode =()=>{
        theme === 'light' ? setTheme('dark') : setTheme('light');
    }
    const [navActive, setNavActive] = useState('nav__menu');
    const navToggle = () =>{
        navActive === 'nav__menu' ? setNavActive('nav__menu nav__active') : setNavActive('nav__menu');
        console.log('clicked');
    }
  return (
    <div className='navbar'>
      <img src={logo} alt="" className='logo'/>
      <ul className={navActive}>
        <li className='nav__item'><a href='#' className='nav__link'>Home</a></li>
        <li className='nav__item'><a href='#' className='nav__link'>Products</a></li>
        <li className='nav__item'><a href='#' className='nav__link'>Features</a></li>
        <li className='nav__item'><a href='#' className='nav__link'>About</a></li>
      </ul>


      <div className='search-box'>
        <input type="text" placeholder='Search'/>
        <img src={theme === 'light' ?search_icon_light :search_icon_dark

        } alt="logo" />
      </div>
      <img src={theme === 'light' ?toggle_light :toggle_dark} alt="" className='toggle-icon' onClick={()=>{toggle_mode()}}/>
      <div onclick ={navToggle} className='nav__toggler'>
        <div className='line1'></div>
        <div className='line2'></div>
        <div className='line3'></div>
      </div>
    </div>

  );
}

export default Navbar;
